import {
	ADD_COLLECTION_SUCCESS
} from '../actions/types';

const initialState = {
	loading: true,
	title: ''
};



export const addCollection = (state = initialState, action) => {
	const { type, payload } = action;
	switch (type) {
		case ADD_COLLECTION_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
	
		default:
			return state;
	}
}

export const getCollections = (state = initialState, action) => {
	const { type, payload } = action;
	switch (type) {
		case GET_COLLECTION_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
	
		default:
			return state;
	}
}
