import { DATA_SUCCESS, DATA_FAILURE } from '../actions/types';
const initialState = {
	data: [],
	loading: true,
	error: {}
};

export default function(state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case DATA_SUCCESS:
			return {
				...state,
				data: payload,
				loading: false
			};
		case DATA_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				data: null
			};
		default:
			return {
				...state
			};
	}
}
