import styled from 'styled-components';

const DataCard = styled.div`
width: 50vw;
max-height: 100%;
border: 1px solid light-grey;
box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.2);
padding: 20px;
margin: 25px auto;
text-align: center;
img {
  margin: 0 auto;
  display block;
  width: 100%;
}
select {
  -webkit-appearance: none;
  -moz-appearance: none;
  -ms-appearance: none;
   -o-appearance: none;
    appearance: none;
  height: 25px;
  border: none;
  border: 1px solid grey;
  border-radius: 4px;
  padding: 4px;
  font-size: 120%;
}
ul {
  list-style: none;
  padding: 0 0 10px 0;
  margin: 0;
  li {
    
    @media(min-width: 600px) {
      font-size: 115%;
      height: 1.8em

    }
    @media(min-width: 800px) {
      font-size: 125%;
      height: 4vh;
    }
    select {
      color: grey; 
			width: 100%;
      height: 4.25vh;
      padding-bottom: 0.5vh;
			font-size: 3vw;
			border-radius: 0;
			border: 1px solid Gainsboro;
			text-align-last:center;
			&:focus { outline: none;}
			option {
				text-align: center;
			}
    }
  }
}
`;

export default DataCard
