import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import PropTypes from 'prop-types';
import { searchNews } from '../../actions/searchNews';
import styled from 'styled-components';
import Logout from '../auth/Logout';
const Container = styled.div`
import axios from 'axios';

	padding: 8px;
	background-color: lightgrey;
	width: 100%;
`;

const Button = styled.div`
	display: block;
	padding: 8px;
	margin: 1em auto;
	max-width: 40%;
	text-align: center;
	font-size: 1em;
	font-weight: 500;
	text-decoration: none;
	letter-spacing: 0.8px;
	color: midnightblue;
	background-color: white;
	transition: color 0.4s;
	border: 1px solid lightcyan;
	border-radius: 4px;

	&:hover {
		text-decoration: none;
		color: orange;
	}
`;

// onsubmit call newsSearch action

const DashBoard = ({ searchNews, isAuthenticated }) => {
	let history = useHistory();
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const routeChange = (axios) => {
		history.push('/landing');
		//delete axios.defaults.headers.common['x-auth-token'];
		localStorage.removeItem('token');
	};

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
	};

	return (
		<Container>
			<div>
				{isAuthenticated ? <button onClick={routeChange}>Logout</button> : ''}
			</div>
			<form className='form' onSubmit={(e) => onSubmit(e)}>
				<input
					type='text'
					placeholder='Search Term'
					name='searchTerm'
					value={searchTerm}
					onChange={onChange}
				/>
				<input type='submit' value='News Search' />
			</form>
		</Container>
	);
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { searchNews })(DashBoard);
