import styled from 'styled-components';

const Input = styled.input`

    border: none;
    height: 5em;
    font-size: 100%;
    width: 15em;
    padding: 0.4em;
    text-align: center;
    border: none;
    color: grey;
  	&:focus {
      outline: none;
    }
    @media(min-width: 800px){
      font-size: 150%;
    }


`

export default Input;
