import React, { Fragment, useState, useEffect } from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import styled from 'styled-components'
import Button from '../../styled/Button';
import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField'
import DataCard from '../layout/DataCard'
import BackButton from '../../styled/BackButton'

const Sticky = styled.div`
	position: -webkit-sticky;
	position: sticky;
	top: 0;
	left: 0;
	background-color: yellow;
	padding: 8em;
	margin-top: 15em;
`

const StyledForm = styled(DataForm)`
	width: 100%;
	padding 10px;
	border-bottom: 1px solid lightcyan;
	background-color: #fff;
`
const Card = styled(DataCard)`
	
`

const Wrapper = styled.div`
	height: 100%;
	width: 100%;
`

const SearchTitle = styled.h3`
	text-align:center;
	letter-spacing: 1.25px;
	margin-bottom: 0.75em;
`
const StyledSubmit = styled(Button)`
	height: 4em;
	width: 15em;
	font-size: 1em;
	margin: 1em auto 1em auto;
	letter-spacing: 2.5px;
	border-radius: 6px;
`;


const Search = ({ logout, deleteUser, isAuthenticated }) => {

	const [searchWord, setSearchTerm] = useState({
		searchTerm: '',
	});

	const { searchTerm } = searchWord;

	const onChange = (e) => {
		setSearchTerm({ ...searchWord, [e.target.name]: e.target.value });
	};

	const onSubmit = async (e) => {
		e.preventDefault();
		console.log({searchTerm})
		//	search(searchTerm);
	};

	const history = useHistory();
 
	const back = () => {
		const path = '/dashboard'
		history.push(path)
	}


/*	return (
		<Wrapper>
			<BackButton onClick={back}></BackButton>
			
				<StyledForm onSubmit={(e) => onSubmit(e)}>
				<Sticky>
					<SearchTitle>Search for Articles</SearchTitle>
					<DataField>
						<input
							type='text'
							name='searchTerm'
							value={searchTerm}
							required
							onChange={(e) => onChange(e)}
							minLength='3'
						/>
					</DataField>
					<StyledSubmit>Search</StyledSubmit>	
					</Sticky>
				</StyledForm>
			

		</Wrapper>

	

		}
	);  */

	const reader = (e) => {
		e.preventDefault()
		console.log('reader')
	}
	document.getElementById("reader").addEventListener("click", reader);
	return (
		<Wrapper>
			<Sticky>
				<StyledForm onSubmit={(e) => onSubmit(e)}>
				<SearchTitle>Search for Articles</SearchTitle>
					<DataField>
							<input
								type='text'
								name='searchTerm'
								value={searchTerm}
								required
								onChange={(e) => onChange(e)}
								minLength='3'
									/>
					</DataField>
					<StyledSubmit>Search</StyledSubmit>	
				</StyledForm>
			</Sticky>
			<DataCard onClick={reader(e)}></DataCard>
			<DataCard></DataCard>
			<DataCard></DataCard>
			<DataCard></DataCard>
			
		</Wrapper>

	)
};

export default Search;
