	// 		type: DATA_FAILURE,
	// 		payload: {
	// 			msg: err,
	// 			status: err
	// 		}
	// 	});
	// }

	try {
		const gaurdianReq = await axios.get(gaurdian);

		if (!gaurdianReq.data.status == 'OK') {
			throw new Error(`${gaurdianReq.status} ${gaurdianReq.statusText}`);
		}

		dispatch({
			type: GAURDIAN_DATA_SUCCESS,
			payload: gaurdianReq
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err,
				status: err
			}
		});
	}
};
