import axios from 'axios';
import { setAlert } from './alert';
import store from '../store';
import {
	REGISTER_SUCCESS,
	REGISTER_FAIL,
	USER_LOADED,
	AUTH_ERROR,
	LOGIN_SUCCESS,
	LOGIN_FAIL,
	LOGOUT,
	DELETE_USER,
} from './types';

// Load User
export const loadUser = () => async (dispatch) => {
	try {
		const res = await axios.get('/auth');

		dispatch({
			type: USER_LOADED,
			payload: res.data,
		});
	} catch (err) {
		dispatch({
			type: AUTH_ERROR,
		});
	}
};

// Register User
export const register = ({ fullname, email, password }) => async (dispatch) => {
	const config = {
		headers: {
			'Content-Type': 'application/json',
		},
	};

	const body = JSON.stringify({ fullname, email, password });
	console.log(body);
	try {
		const res = await axios.post('/user', body, config);
		console.log(res);
		dispatch({
			type: REGISTER_SUCCESS,
			payload: res.data,
		});
		dispatch(loadUser());
	} catch (err) {
		const errors = err.response.data.errors;
		console.log(errors);

		// if (errors) {
		// 	errors.forEach((error) => dispatch(setAlert(error.msg, 'danger')));
		// }

		dispatch({
			type: REGISTER_FAIL,
		});
	}
};

// Login User
export const login = (email, password) => async (dispatch) => {
	const config = {
		headers: {
			'Content-Type': 'application/json',
		},
	};

	const body = JSON.stringify({ email, password });

	try {
		const res = await axios.post('/auth', body, config);

		dispatch({
			type: LOGIN_SUCCESS,
			payload: res.data,
		});

		dispatch(loadUser());
	} catch (err) {
		console.log(err);
		// if (errors) {
		//   errors.forEach((error) => dispatch(setAlert(error.msg, 'danger')));
		// }

		dispatch({
			type: LOGIN_FAIL,
		});
	}
};

// Logout
export const logout = () => (dispatch) => {
	//console.log(state.auth.user._id);
	try {
		dispatch({
			type: LOGOUT,
		});
	} catch (e) {
		console.log(e);
	}
};

// Delete User Account
export const deleteUser = (id) => async (dispatch) => {
	const body = store.getState().auth.token;
	try {
		const res = await axios.delete('/user');

		dispatch({
			type: DELETE_USER,
		});
	} catch (e) {
		console.log(e);
	}
};
