import {
	GET_COLLECTIONS_SUCCESS
} from '../actions/types';

const initialState = {
	loading: true,
	containers: []
};


export default function (state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case GET_COLLECTIONS_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
	
		default:
			return state;
	}
}
