import { DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';

export const getData = () => async dispatch => {
	try {
		/*
    axios.all([
      await axios.get('https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu'),
      await get('https://content.guardianapis.com/world/2020/mar/27/coronavirus-the-week-explained?api-key=3cb25f91-26b8-4f58-833f-873b0478f441')
    ])
    .then(axios.spread((nytRes, gaurdianRes) => {
       console.log(nytRes, gaurdianRes)
    }).catch(error => {
      console.log(error);
    }),
    */
		/*
		const res = await axios.get(
			'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu'
		);
*/

		const res = await axios.get(
			'https://content.guardianapis.com/search?show-fields=thumbnail&q=coronavirus&page-size=200&api-key=3cb25f91-26b8-4f58-833f-873b0478f441'
		);

		console.log(res.data);
		dispatch({
			type: DATA_SUCCESS,
			payload: res.data
			//payload: res.data.content
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err.response.statusText,
				status: err.response.status
			}
		});
	}
};
