import {
  ADD_COLLECTION_SUCCESS,
	ADD_COLLECTION_FAILURE,
	GET_COLLECTIONS
} from './types';

const axios = require('axios')

export const getCollections = (title) => async (dispatch) => {
	try {
		const res = await axios.post('/container');

		dispatch({
			type: GET_COLLECTIONS,
			payload: res.data,
		});
	} catch (err) {
		dispatch({
			type: GET_COLLECTIONS_FAILURE,
		});
	}
};

export const addCollection = (title) => async (dispatch) => {
	try {
		const res = await axios.post('/container');

		dispatch({
			type: ADD_COLLECTION_SUCCESS,
			payload: res.data,
		});
	} catch (err) {
		dispatch({
			type: ADD_COLLECTION_FAILURE,
		});
	}
};
