import React, { useState, useEffect, Fragment } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const DataCard = (topStories) => {
	if (topStories === undefined || null || '') {
		return;
	}

	const allowDrag = (ev) => {
		ev.preventDefault();
	};

	const drag = (ev) => {
		ev.dataTransfer.setData('text', ev.target.id);
		console.log('drag');
	};

	const drop = (ev) => {
		ev.preventDefault();
		var data = ev.dataTransfer.getData('text');
		ev.target.appenChild(document.getElementById(data));
	};

	return (
		<container>
			<Fragment>
				<div
					id='card'
					draggable='true'
					onDragStart='drag(event)'
					style={{
						border: '3px solid green',
						padding: '4em',
						margin: '2em',
						width: '100px',
						height: 'auto',
					}}>
					<h1>Trumps Lies</h1>
					<p>Another day another litany of insults and lies</p>
					<p>image</p>
				</div>
			</Fragment>

			<container>
				<div
					id='target'
					ondrop='drop(event)'
					ondragover='allowDrop(event)'
					style={{ border: '3px solid green', padding: '4em', margin: '2em' }}>
					"Politics"
				</div>
				<div
					id='two'
					style={{ border: '3px solid green', padding: '4em', margin: '2em' }}>
					Business`
				</div>
				this is where you collect cards
			</container>
		</container>
	);
};

DataCard.propTypes = {
	currentResults: PropTypes.array.isRequired,
	searchResults: PropTypes.array.isRequired,
};

const mapStateToProps = (state) => ({
	currentResults: state.topStories.currentResults,
	searchResults: state.searchNews.searchResults,
});

export default connect(mapStateToProps)(DataCard);
