import styled from 'styled-components';

export default styled.form`
	transform: translate(-50%, -50%);
	position: absolute;
	top: 50%;
	left: 50%;
	overflow: auto;
	background-color: whitesmoke;
	text-align: center;
	width: 50%;
	height: auto;
	padding: 2em 0;
`;
