  try { 
    const res = await axios.get('/search-news', {
			params: {
				q: searchTerm,
			},
		});
    dispatch({
      type: ADD_CARDS,
      payload: res.data
    })
  } catch(err) {
    dispatch({
      type: CARD_ERROR,
      payload: { msg: err.response.statusText, status: error.response.status}
    })
  }
}


