import React, { Fragment, useState, useEffect } from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import {searchNews} from '../../actions/searchNews'
import { connect } from 'react-redux';

import styled from 'styled-components'
import Button from '../../styled/Button';
import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField'
import DataCard from '../layout/DataCard'
import BackButton from '../../styled/BackButton'

const StyledForm = styled.form`
	width: 100%;
	text-align: center;
	border-bottom: 1px solid lightcyan;
	background-color: #fff;
	position: fixed;
	padding: 8px 0 1em 0;
	top: 0;
	left: 0;
`
const Header = styled.div`
	display: inline-block;
	width: 100%;
`

const Wrapper = styled.div`
	height: 100%;
	width: 100%;
	margin: 260px 0 0 0;
	padding: 0;
`

const SearchTitle = styled.h3`
	text-align:center;
	letter-spacing: 1.25px;
	margin-bottom: 0.75em;
`
const StyledSubmit = styled(Button)`
	height: 4em;
	width: 15em;
	font-size: 1em;
	margin: 1em auto 1em auto;
	letter-spacing: 2.5px;
	border-radius: 6px;
`;

const Search = ({search, isAuthenticated }) => {

	const [searchWord, setSearchTerm] = useState({
		searchTerm: '',
	});

	const { searchTerm } = searchWord;

	const onChange = (e) => {
		setSearchTerm({ ...searchWord, [e.target.name]: e.target.value });
	};

	const onSubmit = async (e) => {
		e.preventDefault();
		console.log({searchTerm})
		searchNews(searchTerm)
		//	search(searchTerm);
	};

	const history = useHistory();
 
	const back = () => {
		const path = '/dashboard'
		history.push(path)
	}

	return (
		<Wrapper>
				<StyledForm onSubmit={(e) => onSubmit(e)}>
						<SearchTitle>Search for Articles</SearchTitle>
					<DataField>
							<input
								type='text'
								name='searchTerm'
								value={searchTerm}
								required
								onChange={(e) => onChange(e)}
								minLength='3'
									/>
					</DataField>
					<StyledSubmit>Search</StyledSubmit>	
				</StyledForm>

			<div>
			
			</div>
			<BackButton onClick={back}></BackButton>
			
		</Wrapper>

	)
};

const mapStateToProps = (state) => ({
	search: state.searchResults,
	alertMessage: state.alert.alertMessage,
});

export default connect(mapStateToProps, { searchNews })(Search);
