import { GAURDIAN_DATA_SUCCESS, DATA_FAILURE } from '../actions/types';
const initialState = {
	gaurdianData: null,
	loading: true,
	error: {}
};

export default function(state = initialState, action) {
	//console.log(action);
	const { type, payload } = action;

	console.log('gaurdReducer payload', payload);

	switch (type) {
		case GAURDIAN_DATA_SUCCESS:
			return {
				...state,
				gaurdianData: payload,
				loading: false
			};
		case DATA_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				gaurdianData: []
			};
		default:
			return {
				...state
			};
	}
}
