import styled from 'styled-components';

export default styled.form`
	transform: translate(-50%, -50%);
	position: absolute;
	top: 50%;
	left: 50%;
	overflow: auto;
	background-color: whitesmoke;
	text-align: center;
	max-width: 70%;
	width: 50%;
	height: auto;
	padding: 2em 0;

	-webkit-box-shadow: -1px 32px 54px -1px rgba(0, 0, 0, 0.19);
	-moz-box-shadow: -1px 32px 54px -1px rgba(0, 0, 0, 0.19);
	box-shadow: -1px 32px 54px -1px rgba(0, 0, 0, 0.19);
`;
