import axios from 'axios';
import cors from './cors';

let nyt =
	'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu';
let gaurdian =
	'https://content.guardianapis.com/world/2020/mar/27/coronavirus-the-week-explained?api-key=3cb25f91-26b8-4f58-833f-873b0478f441';

const newsHttp = async () => {
	let nytReq = await axios.get(nyt);
	let gaurdianReq = await axios.get(gaurdian);
	(function() {
		var cors_api_host = 'cors-anywhere.herokuapp.com';
		var cors_api_url = 'https://' + cors_api_host + '/';
		var slice = [].slice;
		var origin = window.location.protocol + '//' + window.location.host;
		var open = XMLHttpRequest.prototype.open;
		XMLHttpRequest.prototype.open = function() {
			var args = slice.call(arguments);
			var targetOrigin = '/^https?://([^/]+)/i.exec(args[1])';
			if (
				targetOrigin &&
				targetOrigin[0].toLowerCase() !== origin &&
				targetOrigin[1] !== cors_api_host
			) {
				args[1] = cors_api_url + args[1];
			}
			return open.apply(this, args);
		};
	})();
	console.log('req');
	axios
		.all([nytReq, gaurdianReq])
		.then(
			axios.spread((...responses) => {
				const nytRes = responses[0];
				const gaurdianRes = responses[1];

				//
				console.log([nytRes, gaurdianRes]);

				return [nytRes, gaurdianRes];
			})
		)
		.catch(err => {
			console.log(err);
		});
};

export default newsHttp;
