import styled from 'styled-components';

export default styled.form`
	height: 80%;
	transform: translate(-50%, -50%);
	position: absolute;
	top: 50%;
	left: 50%;
	resize: both;
	overflow: auto;
	background-color: whitesmoke;
	text-align: center;
`;
