import { GAURDIAN_DATA_SUCCESS, DATA_FAILURE } from '../actions/types';
const initialState = {
	gaurdianData: [],
	loading: true,
	error: {}
};

const gaurdian = (state = initialState, action) => {
	const { type, payload } = action;

	switch (type) {
		case GAURDIAN_DATA_SUCCESS:
			return {
				...state,
				gaurdianData: payload,
				loading: false
			};
		case DATA_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				gaurdianData: []
			};
		default:
			return {
				...state
			};
	}
};
