import React, {Fragment} from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField';
import Nav from '../../styled/Nav';
import Button from '../../styled/Button';
import BackButton from '../../styled/BackButton'
import Header from '../../styled/Header'


const OptionsButton = styled(Button)`
	height: 2em;
	width: 4em;
	font-size: 1em;
	margin: 2em auto 1em auto;
	letter-spacing: 2.5px;
	border-radius: 6px;
`;

const CollectionInput = styled(DataField)`
	background-color: #fff;
	display: inline-block;
	width: 3em;
	height: 1em;
	padding: 8px;
	margin: 8px;
`


const Collection = () => {
	const history = useHistory();
	const back = () => {
		const path = '/dashboard'
		history.push(path)
	}
	return (
		<Fragment>
			<Header>
				<Nav>
				<CollectionInput>Add a Collection</CollectionInput>
			<OptionsButton></OptionsButton>
				</Nav>
		
			
		<BackButton onClick={back}></BackButton>
		</Header>
		</Fragment>
	);
};

export default Collection;
