import React, { useState, useEffect, Fragment } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const DataCard = (topStories) => {
	if (topStories === undefined || null || '') {
		return;
	}

	const dragstartHandler = (ev) => {
		ev.dataTransfer.setData('text/plain', ev.target.id);
	};

	window.addEventListener('DOMContentLoaded', () => {
		const element = document.getElementById('card');
		element.addEventListener('dragstart', dragstarthandler);
	});

	const dragover_handler = (ev) => {
		ev.preventDefault();
		ev.dataTransfer.dropEffect = 'move';
	};
	const drop_handler = (ev) => {
		ev.preventDefault();
		// Get the id of the target and add the moved element to the target's DOM
		const data = ev.dataTransfer.getData('text/plain');
		ev.target.appendChild(document.getElementById(droptarget));
	};

	return (
		<container>
			<Fragment>
				<div
					id='card'
					draggable='true'
					onDragStart='dragstart_handler(event)'
					style={{
						border: '3px solid green',
						padding: '4em',
						margin: '2em',
						width: '100px',
						height: 'auto',
					}}>
					<h1>Trumps Lies</h1>
					<p>Another day another litany of insults and lies</p>
					<p>image</p>
				</div>
			</Fragment>

			<container>
				<div
					id='droptarget'
					ondrop='drop_handler(event)'
					ondragover='dragover_handler(event)'
					style={{ border: '3px solid green', padding: '4em', margin: '2em' }}>
					"Politics"
				</div>
				<div
					id='two'
					style={{ border: '3px solid green', padding: '4em', margin: '2em' }}>
					Business`
				</div>
				this is where you collect cards
			</container>
		</container>
	);
};

DataCard.propTypes = {
	currentResults: PropTypes.array.isRequired,
	searchResults: PropTypes.array.isRequired,
};

const mapStateToProps = (state) => ({
	currentResults: state.topStories.currentResults,
	searchResults: state.searchNews.searchResults,
});

export default connect(mapStateToProps)(DataCard);
