import React, { useState, useEffect} from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import {connect} from 'react-redux'
import styled from 'styled-components';


import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField';
import Nav from '../../styled/Nav';
import Button from '../../styled/Button';
import BackButton from '../../styled/BackButton'
import DataCard from '../../styled/DataCard'
import Header from '../../styled/Header'
import {Add} from '@styled-icons/material/Add'
import {DeleteOutline} from '@styled-icons/typicons/DeleteOutline'

import {getCollections} from '../../actions/collection'
import {addCollection} from '../../actions/collection'
import {deleteCollection} from '../../actions/collection'
import {retrieveCards} from '../../actions/card'
import { GET_CARDS } from '../../actions/types';

import Modal from './Modal'

const Wrapper = styled.div`
	height: 100%;
	width: 100%;
	margin: 260px 0 0 0;
	padding: 0;
`

const CollectionInput = styled.div`
	input {
		border: none;
		height: 5em;
		width: 15em;
		padding: 0.4em;
		text-align: center;
		border: 1px solid #85c1e9;
		color: grey;
	}
`

const CollectionNav = styled(Nav)`
	width: 100%;
	text-align: center;
	background-color: #fff;
	position: fixed;
	padding: 16px 0 1em 0;
	top: 0;
	left: 0;
	box-shadow: 2px 2px 8px rgba(0,0,0,0.2);

`

const DeleteButton = styled(DeleteOutline)`
	width: 30px;
`

const Card = styled(DataCard)`
	text-align: center;
	ul{
		list-style: none;
		
	}
	li {
		display: inline-block;
		width: 48%;
		text-align: left;
	}
	li:nth-child(2) { 
		width: 20%;
		text-align: right;
`

const Back = styled(BackButton)`

`
const AddButton = styled(Add)`
	width: 25px;
	padding-right: 18px;
`

const SearchTitle = styled.h5`
	text-align:center;
	letter-spacing: 1.25px;
	margin: 1em 0 1em;
`

const CollectionCard = styled(DataCard)`
  ul {
		display: block;
	}
	li {
		display: inline-block;
		:nth-child(2n) {
			padding-left: 10px;
		}
	}
		
	}
`

const Collection = (props, {addCollection, collections, getCollections, deleteCollection, retrieveCards, cards}) => {
	
	useEffect(() => {
		props.getCollections()
	},[])

	const [formData, setFormData] = useState({
		title: '',
	});

	const { title } = formData;

	const onChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const onSubmit = (e) => {
		console.log("add collection")
		e.preventDefault();

		props.addCollection(title);
	};

	const deleteButton = (e, title) => {
		console.log(title)
		e.preventDefault()
		props.deleteCollection(title) 
		props.getCollections()
	}

	const openCollection = (title) => {
		// call for all cards matching containerTitle
		console.log(title)
		props.retrieveCards(title)
	}

	const history = useHistory();
	const back = () => {
		const path = '/dashboard'
		history.push(path)
	}
	
	return (
		<Wrapper>
			<CollectionNav>
					<CollectionInput>
						<input
						type='title'
						name='title'
						placeholder="Container Title"
						required
						onChange={(e) => onChange(e)}
						minLength='6'
					/></CollectionInput>
				<Button onClick={onSubmit}>
					<AddButton/>
				</Button>
			</CollectionNav>
			
				{props.collections != undefined && props.collections.length > 0 && props.collection != 'Container exists' && (props.collections.map(collection => { 
					
					return(
						<Card onClick={() => openCollection(collection.title)}>							
								<ul >
									<li key={Math.random()}><h6 >{collection.title}</h6></li>
									<li key={Math.random()}><DeleteButton id={collection._id} onClick={(e) => {deleteButton(e, `${collection.title}`)}} /></li>
									{props.cards.map(card => {
										return (
											<a href={card.url} key={card.pub_date} id={card.pub_date}target="_blank">
											<SearchTitle id="title">{card.title}</SearchTitle>
											<img id="img" src={card.img}/>
									 </a>
										)
									})}
								</ul>
								
						</Card>
							
					) 
				}))}
		
			
		
			
		<BackButton onClick={back}></BackButton>
		
		</Wrapper>
	);
};

// export default Collection;

const mapStateToProps = (state) => ({
	collections: state.collection.containers,
	cards: state.card.cards
});

export default connect(mapStateToProps, {addCollection, getCollections, deleteCollection, retrieveCards})(Collection);
