import styled from 'styled-components';

export default styled.input`
  border: grey;
  width: 50vw;
  text-align: center;
  input {
    font-size: 100%;
    background-color: #fff;
    @media(min-width: 750px) {
      font-size: 1.25em;
    }
  }
`;
