import styled from 'styled-components';

export default styled.ul`
	font-family: sans-serif;
	text-align: center;
	display: flex;
	margin: 0;
	padding: 0;
	flex-grow: 0;
	min-height: 4rem;
	width: 100%;
	text-align: center;
	background-color: #fff;
	position: fixed;
	top: 0;
	left: 0;
	box-shadow: 2px 2px 8px rgba(0,0,0,0.2);
	button { color: #fff }
	:hover { color: blue; }
`;
