import styled from 'styled-components';

const Input = styled.div`
  input {
    border: none;
    height: 5em;
    font-size: 100%;
    width: 15em;
    padding: 0.4em;
    text-align: center;
    border: none;
    color: grey;
  	&:focus {
      outline: none;
    }
  }

`

export default Input;
