import React, { Fragment, useState } from 'react';
import { connect } from 'react-redux';
import { register } from '../../actions/auth';
import { Redirect } from 'react-router-dom';

import styled from 'styled-components';
import DataForm from '../../styled/DataForm';
import DataField from '../../styled/DataField';
import GlobalButton from '../../styled/GlobalButton';

const Title = styled.h3`
	letter-spacing: 1.25px;
	margin-bottom: 0.75em;
`;

const StyledSubmit = styled(GlobalButton)`
	height: 3em;
	width: 32em;
	background-color: slateblue;
	color: white;
	letter-spacing: 2.5px;
	&:hover {
		//border: 2px solid slateblue;
		background-color: #aed6f1;
		color: slateblue;
	}
`;

const Register = ({ register, isAuthenticated }) => {
	const [formData, setFormData] = useState({
		fullname: '',
		email: '',
		password: '',
	});

	const { fullname, email, password } = formData;

	const onChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};
	const onSubmit = (e) => {
		e.preventDefault();

		register({ fullname, email, password });
	};

	if (isAuthenticated) {
		return <Redirect to='/login' />;
	}

	return (
		<Fragment>
			<DataForm onSubmit={(e) => onSubmit(e)}>
				<Title>Register</Title>
				<DataField>
					<label htmlFor='fullname'>fullname</label>
					<input
						type='text'
						value={fullname}
						name='fullname'
						onChange={(e) => onChange(e)}
					/>
				</DataField>
				<DataField>
					<label htmlFor='email'>email </label>
					<input
						type='email'
						value={email}
						name='email'
						onChange={(e) => onChange(e)}
					/>
				</DataField>
				<DataField>
					<label htmlFor='Password'>password </label>
					<input
						type='password'
						value={password}
						name='password'
						onChange={(e) => onChange(e)}
					/>
				</DataField>

				<StyledSubmit>Submit</StyledSubmit>
			</DataForm>
		</Fragment>
	);
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { register })(Register);
