import { DATA_FAILURE, NYC_DATA_SUCCESS } from './types';
import axios from 'axios';
import cors from '../utilities/cors';

export const getNyc = () => async dispatch => {
	const nyt =
		'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu';

	//	cors();
	try {
	} catch (err) {}

	const nytReq = await axios.get(nyt);

	//const nycFiltered = nytReq.data.results;

	try {
		dispatch({
			type: NYC_DATA_SUCCESS,
			payload: nytReq
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err,
				status: err
			}
		});
	}
};
