import {
	SEARCH_SUCCESS,
	SEARCH_FAILURE,
	TOP_STORIES_SUCCESS,
	TOP_STORIES_FAILURE,
} from '../actions/types';
const searchInitialState = {
	searchResults: [],
	loading: true,
	error: {},
};

export const searchNews = (state = searchInitialState, action) => {
	const { type, payload } = action;

	switch (type) {
		case SEARCH_SUCCESS:
			return {
				...state,
				searchResults: payload,
				loading: false,
			};
		case SEARCH_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
			};
		default:
			return {
				...state,
			};
	}
};

const topStoriesInitialState = {
	currentResults: [],
	loading: true,
	error: {},
};

export default function (state = topStoriesInitialState, action) {
	const { type, payload } = action;

	switch (type) {
		case TOP_STORIES_SUCCESS:
			return {
				...state,
				currentResults: payload,
				loading: false,
			};

		case TOP_STORIES_FAILURE:
			return {
				...state,
				error: payload,
				loading: false,
				data: [],
			};
		default:
			return {
				...state,
			};
	}
}
