import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import PropTypes from 'prop-types';
import { searchNews } from '../../actions/searchNews';
import { deleteUser } from '../../actions/auth';
import { logout } from '../../actions/auth';
import styled from 'styled-components';
import GlobalButton from '../../styled/GlobalButton';
import Logout from '../auth/Logout';
const Container = styled.div`
import axios from 'axios';

	padding: 8px;
	background-color: lightgrey;
	width: 100%;
`;

const DeleteUser = ({ deleteUser }) => {
	return <GlobalButton onClick={deleteUser}>Delete Account</GlobalButton>;
};

const LogoutUser = ({ logout }) => {
	return <GlobalButton onClick={logout}>Logout</GlobalButton>;
};

const DashBoard = ({ searchNews, isAuthenticated }) => {
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
	};

	return (
		<Container>
			<LogoutUser />
			<DeleteUser />
			<form className='form' onSubmit={(e) => onSubmit(e)}>
				<input
					type='text'
					placeholder='Search Term'
					name='searchTerm'
					value={searchTerm}
					onChange={onChange}
				/>
				<input type='submit' value='News Search' />
			</form>
		</Container>
	);
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { searchNews, deleteUser, logout })(
	DashBoard
);
