import styled from 'styled-components';

export default styled.form`
	transform: translate(-50%, -50%);
	position: absolute;
	top: 50%;
	left: 50%;
	overflow: auto;
	background-color: whitesmoke;
	text-align: center;
	padding: 1.5em 1.5em 2.25em 1.5em;
`;
