import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import PropTypes from 'prop-types';
import { searchNews } from '../../actions/searchNews';
import { deleteUser } from '../../actions/auth';
import LogoutUser from '../auth/LogoutUser';
import DeleteUser from '../auth/DeleteUser';

import styled from 'styled-components';
import GlobalButton from '../../styled/GlobalButton';
import DataField from '../../styled/DataField';

const Container = styled.div`
	margin: 0 auto;
	text-align: center;
	width: 100%;
`;

const AuthOptions = styled.div`
	display: flex;
	justify-content: flex-center;
	align-content: space-around;
`;
const StyledButton = styled(GlobalButton)`
	display: inline-block;
`;

const StyledField = styled(DataField)`
	border: 2px solid #85c1e9;
	height: 3em;
	flex-grow: 1;
	color: slateblue;
	&: label {
		display: inline;
	}
`;

const DashBoard = ({ searchNews, isAuthenticated }) => {
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
	};

	return (
		<Container>
			<AuthOptions>
				<LogoutUser />
				<DeleteUser />
			</AuthOptions>
			<DataField>
				<form className='form' onSubmit={(e) => onSubmit(e)}>
					<input
						type='text'
						name='Search'
						placeholder='Search Term'
						value={searchTerm}
						onChange={onChange}
					/>

					<StyledButton type='submit' value='News Search'>
						Search
					</StyledButton>
				</form>
			</DataField>
		</Container>
	);
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { searchNews })(DashBoard);
