import {
	ADD_COLLECTION_SUCCESS,
	GET_COLLECTION_SUCCESS,
	GET_COLLECTIONS_FAILURE
} from '../actions/types';

const addInitialState = {
	loading: true,
	title: ''
};

const getInitialState = {
	loading: true,
	containers: []
};

export default function  (state = addInitialState, action){
	const { type, payload } = action;
	switch (type) {
		case ADD_COLLECTION_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
		case ADD_COLLECTION_FAILURE:
			return {
				...state,
			};
		case GET_COLLECTION_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
		case GET_COLLECTIONS_FAILURE:
			return {
				...state,
			};
		case DELETE_COLLECTION_SUCCESS:
			return {
				...state,
				containers: payload,
				loading: false,
			};
	
		default:
			return state;
	}		
}
