import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const Alert = ({ alert }) => {
	console.log(alert);
	//return <div key={alert.id}>{alert.msg}</div>;
	if (alert) {
		return 'alert';
	} else {
		return null;
	}
};
const mapStateToProps = (state) => ({
	alert: state.msg,
});

export default connect(mapStateToProps)(Alert);
