import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

// call news-search with search term //

// create search component form

// onsubmit call newsSearch action

const [searchTerm, setSearchTerm] = useState('');

const DashBoard = () => {
	const onChange = (e) => setSearchTerm({ searchTerm: e.target.value });

	const onSubmit = (e) => {
		e.preventDefault();
		//createProfile(formData, history);
	};

	return (
		<form className='form' onSubmit={(e) => onSubmit(e)}>
			<input
				type='text'
				placeholder='Company'
				name='company'
				value={searchTerm}
				onChange={onChange}
			/>
			<input type='submit' value='Search NEws' />
		</form>
	);
};

export default DashBoard;
