import { SEARCH_SUCCESS, SEARCH_FAILURE } from './types';
import axios from 'axios';
import cors from '../utilities/cors';

export const searchNews = (searchTerm) => async (dispatch) => {
	//	cors();
	console.log('search');
	try {
		//let params = { term: searchTerm };
		console.log(params.q);
		const callSearchNews = await axios.get('/search-news', {
			params: {
				q: searchTerm,
			},
		});

		//  no params -- const callSearchNews = await axios.get('/search-news');

		console.log('action', callSearchNews);

		dispatch({
			type: SEARCH_SUCCESS,
			payload: callSearchNews,
		});
	} catch (err) {
		dispatch({
			type: SEARCH_FAILURE,
			payload: {
				msg: err,
				status: err,
			},
		});
	}
};
