import { SEARCH_SUCCESS, SEARCH_FAILURE } from './types';
import axios from 'axios';
import cors from '../utilities/cors';

export const searchNews = () => async (dispatch) => {
	const gaurdian =
		'https://content.guardianapis.com/search?show-fields=thumbnail&q=coronavirus&page-size=100&show-blocks=body:latest&api-key=3cb25f91-26b8-4f58-833f-873b0478f441';

	//	cors();

	try {
		const callSearchNews = await axios.get('/search-news');

		dispatch({
			type: SEARCH_SUCCESS,
			payload: callSearchNews,
		});
	} catch (err) {
		dispatch({
			type: SEARCH_FAILURE,
			payload: {
				msg: err,
				status: err,
			},
		});
	}
};
