import React, { useState, useEffect, Fragment } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import setAuthToken from '../../utils/setAuthToken';

import GlobalLink from '../../styled/GlobalLink';
import styled from 'styled-components';
import { Archive } from '@styled-icons/heroicons-outline/Archive';
import { NewsPaper } from '@styled-icons/zondicons/NewsPaper';
import { FolderOpen } from '@styled-icons/boxicons-regular/FolderOpen';

const Container = styled.div`
	transform: translate(-50%, -50%);
	position: absolute;
	top: 50%;
	left: 50%;
	overflow: auto;
	width: 80%;
`;

const Icons = styled.div`
	width: 300px;
	margin: 0 auto;
	text-align: center;
`;
const StyledFolder = styled(FolderOpen)`
	width: 4em;
	display: inline-block;
	margin-top: 1em;
	padding: 0;
`;

const StyledArchive = styled(Archive)`
	width: 4em;
`;

const StyledNewsPaper = styled(NewsPaper)`
	width: 3em;
`;

const Description = styled.div`
	margin: 0 auto;
	padding: 3em;

	p {
		text-align: center;
		font-size: 2em;
		font-size: 4vw;
	}
`;

const Title = styled.h3`
	font-size: 2.75em;
	font-family: 'Jost', sans-serif;
	padding: 8px 0;
	text-align: center;
	letter-spacing: 1px;
`;

const Styledlink = styled(GlobalLink)``;

const Landing = ({ isAuthenticated }) => {
	if (isAuthenticated) {
		return <Redirect to='/dashboard' />;
	}
	return (
		<Container>
			<Title>The Papers.</Title>
			<Description>
				<p>
					Read news articles from a vast archive of trusted sources and create
					collections for future reading.
				</p>
				<Icons>
					<StyledArchive />
					<StyledNewsPaper />
					<StyledFolder />
				</Icons>
			</Description>
			<Styledlink to='/register'>Create an account</Styledlink>
			<Styledlink to='/login'>Login</Styledlink>
		</Container>
	);
};

Landing.propTypes = {
	//isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	//isAuthenticated: state.auth.isAuthenticated,
});
export default connect(mapStateToProps)(Landing);
