import { DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';
import cors from '../utilities/cors';

export const getData = () => async dispatch => {
	const nyt =
		'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu';
	const gaurdian =
		'https://content.guardianapis.com/search?show-fields=thumbnail&q=coronavirus&page-size=100&api-key=3cb25f91-26b8-4f58-833f-873b0478f441';

	//	cors();

	const nytReq = await axios.get(nyt);
	const gaurdianReq = await axios.get(gaurdian);

	const gaurdianFiltered = gaurdianReq.data.response.results;
	const nytFiltered = nytReq.data.results;

	const indexed = gaurdianFiltered.filter(item => {
		console.log(item.webTitle);
	});
	// console.log(
	// 	gaurdianFiltered.map(item => {
	// 		return item.webTitle;
	// 	})
	// );

	const allData = [];

	allData.push(nytFiltered);
	allData.push(gaurdianFiltered);

	console.log(
		allData[0].map(item => {
			return item;
		})
	);

	try {
		dispatch({
			type: DATA_SUCCESS,
			payload: allData
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err,
				status: err
			}
		});
	}
};
