import styled from 'styled-components';

const GlobalButton = styled.button`
	display: block;
	padding: 8px;
	margin: 1em auto;
	max-width: 40%;
	text-align: center;
	font-size: 1em;
	font-weight: 500;
	text-decoration: none;
	letter-spacing: 0.8px;
	color: midnightblue;
	background-color: white;
	transition: color, font-size 0.4s;
	border: 1px solid lightcyan;
	border-radius: 4px;

	&:hover {
		text-decoration: none;
		color: yellow;
		font-size: 1.25em;
	}
`;

export default GlobalButton;
