import { DATA_FAILURE, NYC_DATA_SUCCESS } from './types';
import axios from 'axios';
import cors from '../utilities/cors';

export const getNyc = () => async dispatch => {
	const nyt =
		'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu';

	//	cors();
	try {
		const nycCall = await axios.get(nyt);

		const jsonResponse = nycCall.json();

		if (!nycCall.data.status === 'OK') {
			throw new Error(`${nycCall.status} ${nycCall.statusText}`);
		}

		console.log(jsonResponse);

		dispatch({
			type: NYC_DATA_SUCCESS
			//payload: nycReq
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err,
				status: err
			}
		});
	}
};
