import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const Alert = ({ alert }) => {
	//return <div key={alert.id}>{alert.msg}</div>;
	if (alert) {
		const msg = alert.msg;
		return { msg };
	} else {
		return null;
	}
};
const mapStateToProps = (state) => ({});

export default connect(mapStateToProps)(Alert);
