import React, { useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { connect } from 'react-redux';

import PropTypes from 'prop-types';
import { searchNews } from '../../actions/searchNews';
import DataView from '../layout/DataView';

import styled from 'styled-components';
import Account from '../auth/Account';
import Nav from '../../styled/Nav';
import Button from '../../styled/Button';
import Content from '../../styled/Content';
import Header from '../../styled/Header';
import Global from '../../styled/Global';
import PageLink from '../../styled/PageLink';

const NavButton = styled(Button)`
	border: none;
`;

const DashBoard = ({ searchNews, isAuthenticated }) => {
	const [searchTerm, setSearchTerm] = useState('');

	const onChange = (e) => setSearchTerm(e.target.value);

	const onSubmit = (e) => {
		e.preventDefault();
		searchNews(searchTerm);
	};

	if (isAuthenticated) {
		return (
			<Global>
				<Header>
					<Nav>
						<PageLink>Search</PageLink>
						<PageLink>Collections</PageLink>

						<PageLink to='/account'>Account</PageLink>
					</Nav>
				</Header>

				<Content />
			</Global>
		);
	}
};

const mapStateToProps = (state) => ({
	searchNews: PropTypes.func.isRequired,
	currentResults: state.topStories.currentResults,
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { searchNews })(DashBoard);

