import { DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';
import newsHttp from '../utilities/http';

export const getData = () => dispatch => {
	let nyt =
		'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu';
	let gaurdian =
		'https://content.guardianapis.com/search?show-fields=thumbnail&q=coronavirus&page-size=100&api-key=3cb25f91-26b8-4f58-833f-873b0478f441';

	let nytReq = axios.get(nyt);
	let gaurdianReq = axios.get(gaurdian);

	//cors();

	axios
		.all([nytReq, gaurdianReq])
		.then(
			axios.spread((...responses) => {
				const nytRes = responses[0];
				const gaurdianRes = responses[1];

				//
				//console.log([nytRes, gaurdianRes]);
				return [nytRes, gaurdianRes];
			})
		)
		.catch(err => {
			console.log(err);
		});

	try {
		dispatch({
			type: DATA_SUCCESS,
			payload: newsRes
			//payload: res.data.content
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg: err.response.statusText,
				status: err.response.status
			}
		});
	}
};
