import { DATA_SUCCESS, DATA_FAILURE } from './types';
import axios from 'axios';

export const getData = () => async dispatch => {
	try {
		const data = [];
		const res = await axios.get(
			'https://api.nytimes.com/svc/topstories/v2/us.json?api-key=vYeKrdCjpgK0YMYXG1yl1VDZ9fhPO7Yu'
		);

		const res2 = await axios.get(
			'https://content.guardianapis.com/world/2020/mar/27/coronavirus-the-week-explained?api-key=3cb25f91-26b8-4f58-833f-873b0478f441'
		);

		//data.append(res);
		data.append(res2);

		console.log(data);
		dispatch({
			type: DATA_SUCCESS
			//payload: r.data
		});
	} catch (err) {
		dispatch({
			type: DATA_FAILURE,
			payload: {
				msg:
					'error' /* msg: err.response.statusText, status: err.response.status */
			}
		});
	}
};
