	width: 
`;


const SearchInput = styled.input`

`
const SearchEntry = styled.div`
	padding: 8px;
	width: 100%;
	margin: 0 auto;
	height: 150px;
`

const SearchTitle = styled.h3`
	text-align:center;
	letter-spacing: 1.25px;
	margin-bottom: 0.75em;
`
const StyledSearch = styled(StyledSubmit)`

`



const Search = ({ logout, deleteUser, isAuthenticated }) => {

	const [searchWord, setSearchTerm] = useState({
		searchTerm: '',
	});

	const { searchTerm } = searchWord;

	const onChange = (e) => {
		setSearchTerm({ ...searchWord, [e.target.name]: e.target.value });
	};

	const onSubmit = async (e) => {
		e.preventDefault();
		console.log({searchTerm})
		//	search(searchTerm);
	};

	return (
		<DataForm onSubmit={(e) => onSubmit(e)}>
					<SearchTitle>Search for Articles</SearchTitle>
					<SearchField>
					<input
						type='text'
						name='searchTerm'
						value={searchTerm}
						required
						onChange={(e) => onChange(e)}
						minLength='3'
					/>
					<StyledSearch>Search</StyledSearch>
					</SearchField>

						
		</DataForm>



	);
};

export default Search;
