import React, { useEffect } from 'react';
//import { Link, withRouter } from 'react-router-dom';
import { getData } from '../actions/data';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const DataView = ({ data, getData }) => {
	useEffect(() => {
		getData();
	}, [getData]);

	console.log(Object.entries(data));

	const allData = Object.entries(data);

	allData.map(newsArr => {
		if (newsArr !== null) {
			console.log(newsArr);
		}
	});

	if (data.data !== null) {
		console.log(data.data);
		data.data.map(items => {
			const news1 = items;
			console.log(news1);
			const news = items.data.results;
			console.log(news);
		});
	}

	// const dataObj = Object.entries(data.data);

	return <h1 onClick={getData}>Reader</h1>;
};

DataView.propTypes = {
	DataView: PropTypes.func.isRequired,
	data: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	data: state.data
});

export default connect(mapStateToProps, { getData })(DataView);
