import axios from 'axios';
import { setAlert } from './alert';
import setAuthToken from '../utils/setAuthToken';
import {
	REGISTER_SUCCESS,
	REGISTER_FAIL,
	USER_LOADED,
	AUTH_ERROR,
	LOGIN_SUCCESS,
	LOGIN_FAIL,
	LOGOUT,
} from './types';

function retryFailedRequest(err) {
	if (err.status === 500 && err.config && !err.config.__isRetryRequest) {
		err.config.__isRetryRequest = true;
		return axios(err.config);
	}
	throw err;
}
axios.interceptors.response.use(undefined, retryFailedRequest);

export const loadUser = () => async (dispatch) => {
	if (localStorage.token) {
		setAuthToken(localStorage.token);
	}
	try {
		const res = await axios.get('/auth', config);
		console.log(res.data);

		dispatch({
			type: USER_LOADED,
			payload: res.data,
		});
	} catch (err) {
		console.log(err);
		dispatch({
			type: AUTH_ERROR,
		});
	}
};

export const register = ({ fullname, email, password }) => async (dispatch) => {
	const config = {
		headers: {
			'Content-Type': 'application/json',
		},
	};

	const body = JSON.stringify({ fullname, email, password });
	console.log('req', body);
	try {
		const res = await axios.post('/user', body, config);
		console.log(res);
		dispatch({
			type: REGISTER_SUCCESS,
			payload: res.data,
		});
		dispatch(loadUser());
	} catch (err) {
		//const errors = err.response.data.errors;
		console.log(err);
		// if (errors) {
		// 	errors.forEach((error) => dispatch(setAlert(error.msg, 'danger')));
		// }

		dispatch({
			type: REGISTER_FAIL,
		});
	}
};

// Login User
export const login = (email, password) => async (dispatch) => {
	const config = {
		headers: {
			'Content-Type': 'application/json',
		},
	};

	const body = JSON.stringify({ email, password });

	try {
		const res = await axios.post('/auth', body, config);

		dispatch({
			type: LOGIN_SUCCESS,
			payload: res.data,
		});
		console.log('login');

		dispatch(loadUser());
	} catch (err) {
		const errors = err.response.data.errors;

		if (errors) {
			console.log(errors);
			//errors.forEach((error) => dispatch(setAlert(error.msg, 'danger')));
		}

		dispatch({
			type: LOGIN_FAIL,
		});
	}
};

// Logout / Clear Profile
export const logout = () => (dispatch) => {
	dispatch({ type: LOGOUT });
	dispatch({ type: LOGOUT });
};
