import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';
import { logout } from '../../actions/auth';

import axios from 'axios';
import Landing from '../layout/Landing';
import GlobalButton from '../../styled/GlobalButton';

const StyledButton = styled(GlobalButton)`
	background-color: white;
	color: slateblue;
	&:hover {
		color: white;
		background-color: slateblue;
	}
`;

const LogoutUser = ({ logout }) => {
	return <StyledButton onClick={logout}>Delete Account</StyledButton>;
};
const mapStateToProps = (state) => ({
	logout,
});

export default connect(mapStateToProps, { logout })(LogoutUser);
