import styled from 'styled-components';

export default styled.ul`
	text-align: center;
	display: flex;
	margin: 0;
	padding: 0;
	flex-grow: 0;
	min-height: 5rem;
	width: 100%;
	background-color: #fff;
	position: fixed;
	top: 0;
	left: 0;
	box-shadow: 2px 2px 8px rgba(0,0,0,0.2);

	a { 
		color: #fff; 
		font-size: 1.25em;
		padding 0.5em 0 0 0;
	  }
`;
